import './CardEditor.css';
import React, {useState} from 'react';

const CardEditor = (props) => {

    const [front, setFront] = useState('')
    const [back, setBack] = useState('')

    console.log(props.cards)

    const cardList = props.cards.map((card) => {
        return(
            <tr>
                <td>{card.front}</td>
                <td>{card.back}</td>
                <td><button>delete card</button></td>
            </tr>
        )
    })

    return (
        <div>
            <h2>Card Editor</h2>
            <table>
                <thead>
                    <th>Front</th>
                    <th>Back</th>
                    <th>Delete</th>
                </thead>
                <tbody>
                    {cardList}
                </tbody>
            </table>

            <br/>
            <input name="front" placeholder='Front of card' value={front}/>
            <input name ="back" placeholder='Back of card' value={back}/>
            <button>add card</button>


        </div>
    );

}

