//import React from 'react';
import React, {useState} from 'react';

const CardViewer = (props) => {

    let front = true;
    let currIndex = 0;

    const switchCard = (index) => {
        if(front){
            return document.getElementById("card").innerHTML = getCardFront(index);
        }
        else{
            return document.getElementById("card").innerHTML = getCardBack(index);
        }  
    }

    const getCardFront = (index) => {
        return props.cards.slice()[index].front;
    }
    const getCardBack = (index) => {
        return props.cards.slice()[index].back;
    }
    const switchSide = (index) => {
        front = !front;
        switchCard(index);
    }
    const newCard = (index) => {
        if(index < 0 || index > props.cards.slice().length - 1){
            return;
        }
        currIndex = index;
        front = true;
        switchCard(index);
    }

    return(
        <div>
            <h2>Card Viewer</h2>
            <div id="card" onClick = {() => switchSide(currIndex)}>{getCardFront(currIndex)}</div>
            <button onClick = {() => newCard(currIndex-1)}>Previous</button>
            <button onClick = {() => newCard(currIndex+1)}>Next</button>
            <button onClick = {() => props.switchMode()}>switch mode</button>
        </div>
    );

}

